 		final AnnotationInstance sqlDeleteAnnotation = JandexHelper.getSingleAnnotation(
 				entityClass.getClassInfo(), HibernateDotNames.SQL_DELETE
 		);
 		entityBinding.setCustomInsert( createCustomSQL( sqlDeleteAnnotation ) );
 
