 		String[] cascadeTypes = oneToMany.value( "cascade" ).asEnumArray();
 		assertArrayEquals( new String[] { "ALL", "DETACH", "PERSIST" }, cascadeTypes );
 		AnnotationInstance manyToOne = annotations.get( JPADotNames.MANY_TO_ONE ).get( 0 );
