 	private boolean isSafeClass(String name) {
 		// classes in any of these packages are safe to load through the "live" ClassLoader
 		return name.startsWith( "java." )
 				|| name.startsWith( "javax." )
 				|| name.startsWith( "org.hibernate." );
 
 	}
 
