 	public boolean equals(Object other) {
 		if ( this == other ) {
 			return true;
 		}
 		if ( other == null || getClass() != other.getClass() ) {
 			return false;
 		}
 
 		final CollectionKey that = (CollectionKey) other;
 		return that.role.equals( role )
 				&& keyType.isEqual( that.key, key, factory );
 	}
 
