 			if ( "toString".equals( methodName ) ) {
 				return "DatabaseMetaData proxy [db-name=" + databaseName + ", version=" + majorVersion + "]";
 			}
 
 			if ( "hashCode".equals( methodName ) ) {
 				return Integer.valueOf( this.hashCode() );
 			}
 
