 		ParentWithCollection otherOtherParent = createParentWithNoChildren( "otherParent" );
 		listeners.clear();
 		Session s = openSession();
 		Transaction tx = s.beginTransaction();
 		parent = ( ParentWithCollection ) s.get( parent.getClass(), parent.getId() );
